import React, { useState } from 'react';

const Navbar = () => {
  const [searchOption, setSearchOption] = useState('See More Categories'); // Default search option

  const handleSearchOptionChange = (e) => {
    setSearchOption(e.target.value);
  };

  return (
    <nav className="bg-blue-500 p-4">
      <div className="container mx-auto flex flex-wrap justify-between items-center">
        {/* Logo */}
        <div className="text-red-900 text-2xl font-bold mb-4 md:mb-0">Logo</div>

        {/* Search Bar with Dropdown */}
        <div className="relative flex-grow">
          <input
            type="text"
            placeholder="Search..."
            className="w-80% p-2 pr-10 rounded-full border "
          />
          <select
            className="absolute top-0 right-0 h-full text-black bg-transparent border-none outline-none md:top-2 md:right-4"
            value={searchOption}
            onChange={handleSearchOptionChange}
          >
            <option value="See More Categories">See More Categories</option>
            <option value="search">Search</option>
            <option value="filter">Filter</option>
          </select>
        </div>

        {/* Login and Signup Buttons */}
        <div className="mt-4 md:mt-0 space-x-4">
          <button className="bg-white text-blue-500 px-4 py-2 rounded">Login</button>
          <button className="bg-blue-500 text-white px-4 py-2 rounded">Signup</button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
