import React from 'react';

const Navbar = () => {
  return (
    <nav className="bg-blue-500 p-4">
      <div className="container mx-auto flex justify-between items-center">
        {/* Logo */}
        <div className="text-white text-2xl font-semibold">
          Your Logo
        </div>

        {/* Search Bar */}
        <div className="flex items-center space-x-4">
          <div className="relative">
            <input
              type="text"
              placeholder="Search..."
              className="py-1 px-3 rounded-full border focus:outline-none focus:border-blue-400"
            />
            <div className="absolute inset-y-0 right-0 flex items-center pr-3 pointer-events-none">
              <svg
                className="h-4 w-4 text-gray-500"
                xmlns="http://www.w3.org/2000/svg"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M15 12a3 3 0 11-6 0 3 3 0 016 0z"
                />
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M17.293 18.293a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414l4-4a1 1 0 111.414 1.414L14.414 12l2.879 2.879a1 1 0 010 1.414z"
                />
              </svg>
            </div>
          </div>
        </div>

        {/* Separator */}
        <div className="h-6 w-px bg-white mx-4"></div>

        {/* Dropdown Menu */}
        <div className="relative">
          <button className="text-white focus:outline-none">
            Dropdown
            <svg
              className="h-4 w-4 ml-1 text-gray-300"
              xmlns="http://www.w3.org/2000/svg"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M9 5l7 7-7 7"
              />
            </svg>
          </button>
          {/* Dropdown Content */}
          <div className="absolute hidden mt-2 bg-white text-gray-800 border border-gray-200 rounded-lg shadow-lg">
            <ul className="py-2">
              <li className="px-4 py-2 hover:bg-blue-100">Option 1</li>
              <li className="px-4 py-2 hover:bg-blue-100">Option 2</li>
              <li className="px-4 py-2 hover:bg-blue-100">Option 3</li>
            </ul>
          </div>
        </div>

        {/* Sign-in and Sign-up Buttons */}
        <div className="flex items-center space-x-4">
          <button className="text-white hover:underline focus:outline-none">
            Sign In
          </button>
          <button className="bg-white text-blue-500 hover:bg-blue-600 py-1 px-4 rounded-full hover:text-white focus:outline-none">
            Sign Up
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
