import React, { useState } from 'react';
import SearchIcon from '../Icon/search-svg.svg';
import logo from "../Icon/Logo.png"

const Navbar = ({ onLoginClick ,onSinupClick}) => {

  const [searchOption, setSearchOption] = useState('See More Categories'); // Default search option

  const handleSearchOptionChange = (e) => {
    setSearchOption(e.target.value);
  };

  return (
    <div className="p-4">
      <div className="container mx-auto flex flex-wrap justify-between items-center">
        {/* Logo */}
        <div className="w-full md:w-auto md:text-center pt-2 pb-4 md:p-0">
          <img src={logo} className="w-40 h-12 pr-2 mx-auto md:mx-0" alt="Logo" />
        </div>
        {/* Search Bar with Dropdown */}
        <div class="rounded-lg border bg-slate-200 flex flex-col md:flex-row  overflow-hidden items-center space-y-2 md:space-y-0 md:space-x-2 w-full md:w-auto">

  <div class="p-4 flex ">
    <img src={SearchIcon} class="w-6 h-6 pr-2" alt="Search Icon" />



  <input
    type="text"
    placeholder="Search"
    class="border-black focus:outline-none bg-slate-200 w-auto flex-grow"
  />
  </div>

  <div class=" border-r-2 border-gray-400 h-6 ls:w-auto hidden md:block  p-4 "></div>
  <div class="ml-2 p-4">
    <select class="bg-transparent outline-none">
      <option>See More Categories</option>
      <option value="search">1</option>
      <option value="filter">2</option>
      <option value="filter">3</option>
      <option value="filter">4</option>
    </select>
  </div>
</div>


        {/* Login and Signup Buttons */}
        <div className="mt-4 md:mt-0 text-center md:text-right w-full">

            <button
              className="bg-white border border-black text-black px-4 py-2 rounded-xl"
              onClick={onLoginClick}
            >
              Login
            </button>
            <button
              className="bg-red-400 text-white px-4 py-2 rounded-xl mt-2 md:mt-0 md:ml-2"
              onClick={onSinupClick}
            >
              Signup
            </button>

        </div>
      </div>
    </div>
  );
};

export default Navbar;
