import React, { useState } from 'react';
import SearchIcon from '../Icon/search-svg.svg';
import LoginForm from "../Pages/Login"
import SinupForm from "../Pages/Register"
import Register from '../Pages/Register';
import Login from '../Pages/Login';

const Navbar = () => {
  const [showLoginForm, setShowLoginForm] = useState(false);
  const [showSinupForm, setShowSinupForm] = useState(false);


  const openLoginForm = () => {
    setShowLoginForm(true);
  };

  const closeLoginForm = () => {
    setShowLoginForm(false);
  };

  const openSinupForm = () => {
    setShowSinupForm(true);
  };

  const closeSinupForm = () => {
    setShowSinupForm(false);
  };
  const [searchOption, setSearchOption] = useState('See More Categories'); // Default search option

  const handleSearchOptionChange = (e) => {
    setSearchOption(e.target.value);
  };

  return (
    <div className="p-4">
      <div className="container mx-auto flex flex-wrap justify-between items-center">
        {/* Logo */}
        <div className="text-red-900 text-2xl font-bold mb-4 md:mb-0">Logo</div>

        {/* Search Bar with Dropdown */}
        <div className="rounded-lg border bg-slate-200 flex items-center space-x-2 w-full md:w-auto">
          {/* Search Icon */}
          <div className="p-4">
            <img src={SearchIcon} className="w-6 h-6 pr-2" alt="Search Icon" />
          </div>

          {/* Input Field */}
          <input
            type="text"
            placeholder="Search"
            className="border-black focus:outline-none bg-slate-200 flex-grow"
          />

          <div className="border-r-2 border-gray-400 h-6 p-4"></div>
          <div className="ml-2 p-4">
            <select className="bg-transparent outline-none">
              <option>See More Categories</option>
              <option value="search">1</option>
              <option value="filter">2</option>
              <option value="filter">3</option>
              <option value="filter">4</option>
            </select>
          </div>
        </div>

        {/* Login and Signup Buttons */}
        <div className="mt-4 md:mt-0 space-x-4">
          <button className="bg-white border border-black text-black px-4 py-2 rounded-xl" onClick={openLoginForm}>
            Login
          </button>
          <button className="bg-red-400 text-white px-4 py-2 rounded-xl" onClick={openSinupForm} >Signup</button>
        </div>
        {showLoginForm && <Login onClose={closeLoginForm} />}
        {showSinupForm && <Register onClose={closeSinupForm} />}
      </div>
    </div>
  );
};

export default Navbar;
