import React from 'react';

const Navbar = () => {
  return (
    <nav className="bg-gray-800 text-white py-4">
      <div className="container mx-auto flex justify-between items-center">
        {/* Logo */}
        <div className="text-xl font-bold">
          <a href="/">Your Logo</a>
        </div>

        {/* Search Bar */}
        <div className="relative">
          <input
            type="text"
            placeholder="Search"
            className="bg-gray-700 text-white py-1 px-3 pr-10 rounded-full"
          />
          <span className="absolute top-0 right-0 mt-2 mr-3">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-6 w-6 text-gray-400"
              viewBox="0 0 20 20"
              fill="currentColor"
            >
              <path
                fillRule="evenodd"
                d="M9.293 2.293a1 1 0 011.414 0l5 5a1 1 0 01-1.414 1.414l-5-5a1 1 0 010-1.414zM10 17a7 7 0 110-14 7 7 0 010 14z"
                clipRule="evenodd"
              />
            </svg>
          </span>
        </div>

        {/* Separator */}
        <div className="h-6 w-px bg-gray-600 mx-4"></div>

        {/* Dropdown Menu */}
        <div className="relative group">
          <button className="text-gray-400 hover:text-white focus:outline-none">
            Menu
          </button>
          <ul className="hidden absolute z-10 left-0 mt-2 space-y-2 bg-gray-800 text-white text-sm group-hover:block">
            <li>
              <a href="#">Item 1</a>
            </li>
            <li>
              <a href="#">Item 2</a>
            </li>
            {/* Add more menu items */}
          </ul>
        </div>

        {/* Sign In/Sign Up Buttons */}
        <div>
          <button className="bg-blue-500 text-white px-4 py-2 rounded-full mr-4">
            Sign In
          </button>
          <button className="bg-green-500 text-white px-4 py-2 rounded-full">
            Sign Up
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
