import React from 'react';

const Navbar = () => {
  return (
    <nav className="bg-gray-800 text-white py-4">
      <div className="container mx-auto flex flex-wrap items-center justify-between">
        {/* Logo */}
        <div className="text-xl font-bold">
          <a href="/">Your Logo</a>
        </div>

        {/* Toggle button for small screens */}
        <div className="block lg:hidden">
          <button
            className="text-gray-400 hover:text-white focus:outline-none"
            onClick={() => {
              // Toggle menu visibility here
            }}
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              className="h-6 w-6"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth="2"
                d="M4 6h16M4 12h16M4 18h16"
              />
            </svg>
          </button>
        </div>

        {/* Menu items */}
        <div className="w-full block flex-grow lg:flex lg:items-center lg:w-auto hidden">
          <div className="text-sm lg:flex-grow">
            <a href="#" className="block mt-4 lg:inline-block lg:mt-0 text-gray-400 hover:text-white mr-4">
              Item 1
            </a>
            <a href="#" className="block mt-4 lg:inline-block lg:mt-0 text-gray-400 hover:text-white mr-4">
              Item 2
            </a>
            {/* Add more menu items */}
          </div>
        </div>

        {/* Sign In/Sign Up Buttons */}
        <div className="mt-4 lg:mt-0">
          <button className="bg-blue-500 text-white px-4 py-2 rounded-full mr-4">
            Sign In
          </button>
          <button className="bg-green-500 text-white px-4 py-2 rounded-full">
            Sign Up
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
