import React, { useState } from 'react';

const Navbar = () => {
  const [searchOption, setSearchOption] = useState('search'); // Default search option

  const handleSearchOptionChange = (e) => {
    setSearchOption(e.target.value);
  };

  return (

    <nav className=" p-4">
      <div className="container mx-auto flex justify-between items-center">
        {/* Logo */}
        <div className="text-red-900 text-2xl font-bold">Logo</div>

        {/* Search Bar with Dropdown */}
        <div className='relative rounded-full border border-gray-500'>
        <div className=" bg-slate-400">
    <svg xmlns="http://www.w3.org/2000/svg" class="absolute inset-y-0 my-auto h-8 w-12 border-r border-transparent stroke-gray-500 px-3.5 peer-focus:border-lime-300 peer-focus:stroke-lime-500500" fill="none" viewBox="0 0 24 24" stroke="currentColor" stroke-width="2">
    <path stroke-linecap="round" stroke-linejoin="round" d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
  </svg>
          <input
            type="text"
            placeholder="Search..."
            className="w-full p-2 pr-40 "
          />



          <select
            className="absolute top-0 right-3 h-full bg-white text-black border-2 border-l-black "
            value={searchOption}
            onChange={handleSearchOptionChange}
          >
            <option value="search">Search</option>
            <option value="filter">Filter</option>
          </select>
        </div>
        </div>


        {/* Login and Signup Buttons */}
        <div className="space-x-4">
          <button className="bg-white text-blue-500 px-4 py-2 rounded">Login</button>
          <button className="bg-blue-500 text-white px-4 py-2 rounded">Signup</button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
