import React, { useState, useEffect } from 'react';

const MenuBar = () => {
  const [showMenu, setShowMenu] = useState(true);

  useEffect(() => {
    // Add an event listener to detect screen size changes
    const handleResize = () => {
      setShowMenu(window.innerWidth >= 1280); // Set to xl breakpoint
    };

    // Initial setup
    handleResize();

    // Attach event listener
    window.addEventListener('resize', handleResize);

    // Cleanup when component unmounts
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <div className="container mx-auto">
      <nav className="pt-5">
        <ul className={`flex flex-col lg:flex-row space-x-10 text-black ${showMenu ? 'block' : 'hidden'}`}>
          <li className="px-0 py-2 xl:px-0 opacity-40 font-semibold">20,172 Result</li>
          <ul className="flex flex-col xl:flex-row space-x-6 text-black">
          <li className=" hover:bg-red-200 transition duration-300 ease-in-out rounded-xl px-4 py-2 xl:px-6 opacity-40 font-semibold">
          Discover
        </li>
        <li className=" hover:bg-red-200 transition duration-300 ease-in-out rounded-xl px-4 py-2 opacity-40 font-semibold">
          Real Estate
        </li>
        <li className=" hover:bg-red-200 transition duration-300 ease-in-out rounded-xl px-4 py-2 opacity-40 font-semibold">
          Moters
        </li>
